import Parse = require("parse/node");
import { Retailer } from './Retailer';
import { User } from './User';

export class Api extends Parse.Object {
  constructor(attributes?: any) {
    super('Api', attributes)
  }

  get gatewayKey(): string { return this.get('gatewayKey') }
  set gatewayKey(value: string) { this.set('gatewayKey', value) }

  get gatewayPlan(): string { return this.get('gatewayPlan') }
  set gatewayPlan(value: string) { this.set('gatewayPlan', value) }

  get apiUsers(): Array<User> { return this.get('apiUsers') }
  set apiUsers(value: Array<User>) { this.set('apiUsers', value) }

  get retailer(): Retailer { return this.get('retailer') }
  set retailer(value: Retailer) { this.set('retailer', value) }
}

Parse.Object.registerSubclass('Api', Api);