import Parse = require("parse/node");
import { OmniChatSchema } from './Schema'

export class Associated extends Parse.Object {
  constructor(attributes?: any) {
    super('Associated', attributes)
  }

  /** the user that owns this association*/
  get user(): Parse.User { return this.get('user') }
  set user(value: Parse.User) { this.set('user', value) }

  /** the default fee that this associated gets for every new retailer*/
  get defaultCommission(): number { return this.get('defaultCommission') }
  set defaultCommission(value: number) { this.set('defaultCommission', value) }
}

Parse.Object.registerSubclass('Associated', Associated);