import Parse = require("parse/node");
import { OmniChatSchema } from './Schema'

export class Boleto extends Parse.Object {
    constructor(attributes?: any) {
        super('Boleto', attributes)
    }

    get lineCode(): string { return this.get('lineCode') }
    set lineCode(value: string) { this.set('lineCode', value) }

    get nossoNumero(): string { return this.get('nossoNumero') }
    set nossoNumero(value: string) { this.set('nossoNumero', value) }

    get documentNumber(): string { return this.get('documentNumber') }
    set documentNumber(value: string) { this.set('documentNumber', value) }

    get url(): string { return this.get('url') }
    set url(value: string) { this.set('url', value) }

    get expirationDate(): Date { return this.get('expirationDate') }
    set expirationDate(value: Date) { this.set('expirationDate', value) }
}

Parse.Object.registerSubclass('Boleto', Boleto);