import Parse = require("parse/node");
import { OmniChatSchema } from './Schema'
import { Retailer } from './Retailer';

export class Branch extends Parse.Object {
    constructor(attributes?: any) {
        super('Branch', attributes)
    }

    get name(): string { return this.get('name') }
    set name(value: string) { this.set('name', value) }

    get addressLine1(): string { return this.get('addressLine1') }
    set addressLine1(value: string) { this.set('addressLine1', value) }

    get addressLine2(): string { return this.get('addressLine2') }
    set addressLine2(value: string) { this.set('addressLine2', value) }

    get number(): string { return this.get('number') }
    set number(value: string) { this.set('number', value) }

    get suburb(): string { return this.get('suburb') }
    set suburb(value: string) { this.set('suburb', value) }

    get city(): string { return this.get('city') }
    set city(value: string) { this.set('city', value) }

    get addressState(): string { return this.get('addressState') }
    set addressState(value: string) { this.set('addressState', value) }

    get country(): string { return this.get('country') }
    set country(value: string) { this.set('country', value) }

    get zip(): string { return this.get('zip') }
    set zip(value: string) { this.set('zip', value) }

    get retailer(): Retailer { return this.get('retailer') }
    set retailer(value: Retailer) { this.set('retailer', value) }

}

Parse.Object.registerSubclass('Branch', Branch);