import Parse = require("parse/node");
import { OmniChatSchema } from './Schema'
import { Collection } from './Collection';
import { Item } from './Item';
import { Order } from './Order';
import { CustomerSupportRequest } from './CustomerSupportRequest';

export class CollectionSelection extends Parse.Object {
    constructor(attributes?: any) {
        super('CollectionSelection', attributes)
    }

    get customerSupportRequest(): CustomerSupportRequest { return this.get('customerSupportRequest') }
    set customerSupportRequest(value: CustomerSupportRequest) { this.set('CustomerSupportRequest', value) }

    get collection(): Collection { return this.get('collection') }
    set collection(value: Collection) { this.set('collection', value) }

    get items(): Array<Item> { return this.get('items') }
    set items(value: Array<Item>) { this.set('items', value) }

    get order(): Order { return this.get('order') }
    set order(value: Order) { this.set('order', value) }
}

Parse.Object.registerSubclass('CollectionSelection', Collection);