import Parse = require("parse/node")
import { OmniChatSchema } from './Schema'
import { Address } from './Address'

export class CreditCard extends Parse.Object {
    constructor(attributes?: any) {
        super('CreditCard', attributes)
    }

    get fullName(): string { return this.get('fullName') }
    set fullName(value: string) { this.set('fullName', value) }

    get birthDate(): Date { return this.get('birthDate') }
    set birthDate(value: Date) { this.set('birthDate', value) }

    get phoneNumber(): string { return this.get('phoneNumber') }
    set phoneNumber(value: string) { this.set('phoneNumber', value) }

    get taxId(): string { return this.get('taxId') }
    set taxId(value: string) { this.set('taxId', value) }

    get taxDocumentType(): OmniChatSchema.TaxDocumentType { return this.get('taxDocumentType') }
    set taxDocumentType(value: OmniChatSchema.TaxDocumentType) { this.set('taxDocumentType', value) }

    get brand(): string { return this.get('brand') }
    set brand(value: string) { this.set('brand', value) }

    get last4(): string { return this.get('last4') }
    set last4(value: string) { this.set('last4', value) }

    get hash(): string { return this.get('hash') }
    set hash(value: string) { this.set('hash', value) }

    get billingAddress(): Address { return this.get('billingAddress') }
    set billingAddress(value: Address) { this.set('billingAddress', value) }
}

Parse.Object.registerSubclass('CreditCard', CreditCard);