import Parse = require("parse/node");
import { OmniChatSchema } from './Schema'
import { Retailer } from './Retailer';

export class FBMessenger extends Parse.Object {
  constructor(attributes?: any) {
    super('FBMessenger', attributes)
  }

  /** Integration status  */
  get status(): OmniChatSchema.FBMessengerStatus { return this.get('status') }
  set status(value: OmniChatSchema.FBMessengerStatus) { this.set('status', value) }

  get pageId(): string { return this.get('name') }
  set pageId(value: string) { this.set('name', value) }

  get pageName(): string { return this.get('pageName') }
  set pageName(value: string) { this.set('pageName', value) }

  get token(): string { return this.get('token') }
  set token(value: string) { this.set('token', value) }

  get retailer(): Retailer { return this.get('retailer') }
  set retailer(value: Retailer) { this.set('retailer', value) }

}

Parse.Object.registerSubclass('FBMessenger', FBMessenger);