import Parse = require("parse/node");
import { OmniChatSchema } from './Schema'

export class MoipSecure extends Parse.Object {
    constructor(attributes?: any) {
        super('MoipSecure', attributes)
    }

    get accessToken(): string { return this.get('accessToken') }
    set accessToken(value: string) { this.set('accessToken', value) }

    get percentageFee(): number { return this.get('percentageFee') }
    set percentageFee(value: number) { this.set('percentageFee', value) }

    get fixedFee(): number { return this.get('fixedFee') }
    set fixedFee(value: number) { this.set('fixedFee', value) }

    /** 
     * Maximum amount that we are going to charge.
     * Absolute value
     */
    get maxFee(): number { return this.get('maxFee') }
    set maxFee(value: number) { this.set('maxFee', value) }
}

Parse.Object.registerSubclass('MoipSecure', MoipSecure);