import Parse = require("parse/node");
import { OmniChatSchema } from './Schema'
import { User } from './User';

export class Note extends Parse.Object {
  constructor(attributes?: any) {
    super('Note', attributes)
  }

  get private(): boolean { return this.get('private') }
  set private(value: boolean) { this.set('private', value) }

  get text(): number { return this.get('text') }
  set text(value: number) { this.set('text', value) }

  get salesPerson(): User { return this.get('salesPerson') }
  set salesPerson(value: User) { this.set('salesPerson', value) }
}

Parse.Object.registerSubclass('Note', Note);