import Parse = require("parse/node");
import { OmniChatSchema } from './Schema'

import { Variant } from './Variant';
import { PricingTable } from './PricingTable';

export class Price extends Parse.Object {
    constructor(attributes?: any) {
        super('Price', attributes)
    }

    get price(): number { return this.get('price') }
    set price(value: number) { this.set('price', value) }

    get salePrice(): number { return this.get('salePrice') }
    set salePrice(value: number) { this.set('salePrice', value) }

    get salePriceStart(): Date { return this.get('salePriceStart') }
    set salePriceStart(value: Date) { this.set('salePriceStart', value) }

    get salePriceEnd(): Date { return this.get('salePriceEnd') }
    set salePriceEnd(value: Date) { this.set('salePriceEnd', value) }

    get variant(): Variant { return this.get('variant') }
    set variant(value: Variant) { this.set('variant', value) }

    get pricingTable(): PricingTable { return this.get('pricingTable') }
    set pricingTable(value: PricingTable) { this.set('pricingTable', value) }
}

Parse.Object.registerSubclass('Price', Price);
