import { Retailer } from './Retailer';
import Parse = require("parse/node");
import { OmniChatSchema } from './Schema'


export class PricingTable extends Parse.Object {
    constructor(attributes?: any) {
        super('PricingTable', attributes)
    }

    /** name of this princing table */
    get name(): string { return this.get('name') }
    set name(value: string) { this.set('name', value) }

    /** pointer to the owner of this pricing table */
    get retailer(): Retailer { return this.get('retailer') }
    set retailer(value: Retailer) { this.set('retailer', value) }

    /** true when this is the default pricing table */
    get default(): boolean { return this.get('default') }
    set default(value: boolean) { this.set('default', value) }

    /** id referente a este objeto quando integrado com um gateway externo */
    get externalId(): string { return this.get('externalId') }
    set externalId(value: string) { this.set('externalId', value) }
}

Parse.Object.registerSubclass('PricingTable', PricingTable);