import Parse = require("parse/node");
import { OmniChatSchema } from './Schema'
import { Retailer } from './Retailer';

export class Quote extends Parse.Object {
  constructor(attributes?: any) {
    super('Quote', attributes)
  }

  /** the text that will be exported to the customer */
  get text(): string { return this.get('text') }
  set text(value: string) { this.set('text', value) }

  /** the title of this quote*/
  get title(): string { return this.get('title') }
  set title(value: string) { this.set('title', value) }

  /** the associated that owns this association*/
  get retailer(): Retailer { return this.get('retailer') }
  set retailer(value: Retailer) { this.set('retailer', value) }
}

Parse.Object.registerSubclass('Quote', Quote);