import Parse = require("parse/node");
import { OmniChatSchema } from './Schema'

import { Order } from './Order';
import { Retailer } from './Retailer';

export class RetailerPayment extends Parse.Object {
    constructor(attributes?: any) {
        super('RetailerPayment', attributes)
    }

    get order(): Order { return this.get('order') }
    set order(value: Order) { this.set('order', value) }

    get retailer(): Retailer { return this.get('retailer') }
    set retailer(value: Retailer) { this.set('retailer', value) }

    get invoiceURL(): string { return this.get('invoiceURL') }
    set invoiceURL(value: string) { this.set('invoiceURL', value) }

    get dueDate(): Date { return this.get('dueDate') }
    set dueDate(value: Date) { this.set('dueDate', value) }

    get amount(): number { return this.get('amount') }
    set amount(value: number) { this.set('amount', value) }
}

Parse.Object.registerSubclass('RetailerPayment', RetailerPayment);